$(document).ready(function () {
    let data = w_api;

    var weatherType = data.weather[0].description;
    var windSpeed = (data.wind.speed * 3.6).toFixed(1);

    var kTemp = data.main.temp;
    let minTemp = data.main.temp_min;
    let maxTemp = data.main.temp_max;
    document.getElementById('temp').innerHTML = `${kTemp}&deg;`;
    document.getElementById('rangeTemp').innerHTML = `${maxTemp}&deg; / ${minTemp}&deg;`;

    var city = data.name;
    document.getElementById('city').innerHTML = city;
    document.getElementById('weatherType').innerHTML = weatherType;
    document.getElementById('windSpeed').innerHTML = `Wind Speed: ${windSpeed} km/h`;

    var showIcon = data.weather[0].icon;
    var api2 = 'http://openweathermap.org/img/w/';

    $('#city').html(city);
    $('#weatherType').html(
        '<img src=' + api2 + showIcon + '.png' + '>' + ' ' + weatherType
    );

});